// En tu archivo src/App.js

import React, { useState } from 'react';
// ... (Importa tus juegos: Gusanito y Memoria)
import MemoryGame from './games/Memory/MemoryGame.js'; // Aseg√∫rate que la ruta sea correcta
// ... (A√±adimos los nuevos componentes)
import Contacto from './pages/Contacto';
import AcercaDe from './pages/AcercaDe';
import PoliticaPrivacidad from './pages/PoliticaPrivacidad';
// ... (El resto de tus imports)


function App() {
  const [selectedGame, setSelectedGame] = useState('home');

  // ... (Tu componente Home deber√≠a lucir similar a esto)
  const Home = () => (
    <div className="home-container">
      {/* ... (Tus botones de juegos existentes) ... */}
      <button onClick={() => setSelectedGame('memory')}>Juego de Memoria üß†</button>
      <button onClick={() => setSelectedGame('gusanito')}>Juego del Gusanito üêç</button>
      
      {/* üöÄ NUEVOS ENLACES DE SOPORTE üöÄ */}
      <div style={{ marginTop: '30px', borderTop: '1px solid #ddd', paddingTop: '20px' }}>
          <button style={{ marginRight: '10px' }} onClick={() => setSelectedGame('about')}>Acerca de Nosotros</button>
          <button style={{ marginRight: '10px' }} onClick={() => setSelectedGame('contact')}>Contacto</button>
          <button onClick={() => setSelectedGame('privacy')}>Pol√≠tica de Privacidad</button>
      </div>
    </div>
  );


  switch (selectedGame) {
    case 'memory':
      return <MemoryGame setSelectedGame={setSelectedGame} />;
    case 'gusanito':
      // return <GusanitoGame setSelectedGame={setSelectedGame} />;
      return <h1>Juego del Gusanito (Pendiente)</h1>; // Ejemplo
    case 'about': // üöÄ NUEVO CASE
      return <AcercaDe setSelectedGame={setSelectedGame} />;
    case 'contact': // üöÄ NUEVO CASE
      return <Contacto setSelectedGame={setSelectedGame} />;
    case 'privacy': // üöÄ NUEVO CASE
      return <PoliticaPrivacidad setSelectedGame={setSelectedGame} />;
    case 'home':
    default:
      return <Home />;
  }
}

export default App;
