import React, { useState, useEffect, useCallback } from 'react';
import './SnakeGame.css'; 

// --- Configuraciones del Juego ---
const BOARD_SIZE = 20; // 20x20 celdas
const INITIAL_SPEED = 200; // 200 ms (milisegundos)

// --- Posiciones y Direcci贸n ---
const INITIAL_SNAKE = [[10, 10]]; 
const INITIAL_FOOD = [5, 5];

const DIRECTIONS = {
  UP: [0, -1],   
  DOWN: [0, 1],  
  LEFT: [-1, 0], 
  RIGHT: [1, 0], 
};

// --- Funci贸n para generar comida aleatoria ---
const generateRandomFood = (currentSnake) => {
  let newFood;
  do {
    // Genera coordenadas aleatorias dentro del tablero
    newFood = [
      Math.floor(Math.random() * BOARD_SIZE),
      Math.floor(Math.random() * BOARD_SIZE)
    ];
    // Asegura que la comida no aparezca sobre el gusano
  } while (currentSnake.some(([sx, sy]) => sx === newFood[0] && sy === newFood[1]));
  return newFood;
};


function SnakeGame() {
  const [snake, setSnake] = useState(INITIAL_SNAKE);
  const [food, setFood] = useState(INITIAL_FOOD);
  const [direction, setDirection] = useState(DIRECTIONS.RIGHT);
  const [isGameOver, setIsGameOver] = useState(false);
  const [score, setScore] = useState(0);
  const [speed, setSpeed] = useState(INITIAL_SPEED);

  // ----------------------------------------------------
  // LGICA DE MOVIMIENTO
  // ----------------------------------------------------
  const moveSnake = useCallback(() => {
    if (isGameOver) return;

    const head = snake[0];
    const newHead = [head[0] + direction[0], head[1] + direction[1]];

    // 1. Colisi贸n con Paredes
    if (
      newHead[0] < 0 ||
      newHead[0] >= BOARD_SIZE ||
      newHead[1] < 0 ||
      newHead[1] >= BOARD_SIZE
    ) {
      setIsGameOver(true);
      return;
    }

    // 2. Colisi贸n consigo mismo (revisa el cuerpo, no la cabeza)
    if (snake.some(([sx, sy], index) => index > 0 && sx === newHead[0] && sy === newHead[1])) {
      setIsGameOver(true);
      return;
    }

    const newSnake = [newHead, ...snake];

    // 3. Comer Comida
    if (newHead[0] === food[0] && newHead[1] === food[1]) {
      // El gusano crece (no se corta la cola)
      setScore(s => s + 1);
      setFood(generateRandomFood(newSnake));
      setSpeed(s => Math.max(50, s - 5)); // Acelera un poco, con un m铆nimo de 50ms
    } else {
      // El gusano se mueve (se corta la cola)
      newSnake.pop();
    }

    setSnake(newSnake);
  }, [snake, direction, food, isGameOver]);


  // ----------------------------------------------------
  // GAME LOOP (Bucle del Juego)
  // ----------------------------------------------------
  useEffect(() => {
    if (isGameOver) return; 
    const timerId = setTimeout(moveSnake, speed);
    return () => clearTimeout(timerId); 
  }, [moveSnake, isGameOver, speed]);


  // ----------------------------------------------------
  // MANEJO DE TECLADO
  // ----------------------------------------------------
  useEffect(() => {
    const handleKeyDown = (event) => {
      const [dx, dy] = direction;

      switch (event.key) {
        case 'ArrowUp':
          if (dy === 0) setDirection(DIRECTIONS.UP); 
          break;
        case 'ArrowDown':
          if (dy === 0) setDirection(DIRECTIONS.DOWN);
          break;
        case 'ArrowLeft':
          if (dx === 0) setDirection(DIRECTIONS.LEFT);
          break;
        case 'ArrowRight':
          if (dx === 0) setDirection(DIRECTIONS.RIGHT);
          break;
        case 'r': // Reiniciar el juego
        case 'R':
          if (isGameOver) resetGame();
          break;
        default:
          break;
      }
    };
    
    document.addEventListener('keydown', handleKeyDown);
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [direction, isGameOver]); 

  // ----------------------------------------------------
  // FUNCIN PARA REINICIAR
  // ----------------------------------------------------
  const resetGame = () => {
    setSnake(INITIAL_SNAKE);
    setFood(generateRandomFood(INITIAL_SNAKE));
    setDirection(DIRECTIONS.RIGHT);
    setIsGameOver(false);
    setScore(0);
    setSpeed(INITIAL_SPEED);
  };


  // ----------------------------------------------------
  // RENDERIZADO DEL TABLERO
  // ----------------------------------------------------
  const renderBoard = () => {
    let cells = [];
    for (let y = 0; y < BOARD_SIZE; y++) {
      for (let x = 0; x < BOARD_SIZE; x++) {
        let className = 'cell';
        let content = null;

        const isSnake = snake.some(([sx, sy]) => sx === x && sy === y);
        const isHead = snake[0][0] === x && snake[0][1] === y;
        
        if (isSnake) {
          className += ' snake-body'; 
          if (isHead) {
            className += ' snake-head'; 
          }
        }

        if (food[0] === x && food[1] === y) {
          className += ' food';
          content = <div className="food-inner"></div>; 
        }
        
        cells.push(<div key={`${x}-${y}`} className={className}>{content}</div>);
      }
    }
    return cells;
  };

  return (
    <div className="game-container">
      <h1> The Snake Game</h1>
      <div 
        className="board" 
        style={{ 
          // Ajustamos el tama帽o del grid al n煤mero de celdas
          gridTemplateColumns: `repeat(${BOARD_SIZE}, 1fr)`,
          gridTemplateRows: `repeat(${BOARD_SIZE}, 1fr)`
        }}
      >
        {renderBoard()}
      </div>
      <h2>Puntuaci贸n: {score}</h2>
      {isGameOver && (
        <div className="game-over">
          隆Game Over! Puntuaci贸n: {score}. Presiona **R** para reiniciar.
        </div>
      )}
    </div>
  );
}

export default SnakeGame;
