import React, { useState, useEffect, useCallback } from 'react';
import './MemoryGame.css';

// --- CONFIGURACI√ìN ---
const CARD_ICONS = ['üçé', 'üçå', 'üçá', 'üçâ', 'üçì', 'ü•ù', 'üçç', 'ü•≠'];
const MAX_ATTEMPTS = 30; // L√≠mite de movimientos
const FLIP_BACK_DELAY = 1200; // Tiempo en milisegundos (1.2 segundos) para que se cierren

// --- FUNCI√ìN DE INICIALIZACI√ìN ---
const initializeCards = () => {
  let cards = [...CARD_ICONS, ...CARD_ICONS];
  
  // Barajar
  for (let i = cards.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [cards[i], cards[j]] = [cards[j], cards[i]];
  }

  // Mapear a objetos de estado
  return cards.map((icon, index) => ({
    id: index,
    icon: icon, 
    isFlipped: false, 
    isMatched: false,
  }));
};


function MemoryGame({ setSelectedGame }) {
  const [cards, setCards] = useState(initializeCards);
  const [flippedCards, setFlippedCards] = useState([]); 
  const [matches, setMatches] = useState(0); 
  const [attemptsLeft, setAttemptsLeft] = useState(MAX_ATTEMPTS); 
  const [isBlocking, setIsBlocking] = useState(false); // Bloquea clics
  const [isGameOver, setIsGameOver] = useState(false);

  // --- L√ìGICA DE COMPARACI√ìN (Separada para mayor claridad y control) ---
  const compareCards = useCallback(() => {
    setIsBlocking(true); 

    const [id1, id2] = flippedCards;
    // ‚ö†Ô∏è CLAVE DE CORRECCI√ìN: Usamos el estado 'cards' actual para la comparaci√≥n
    const card1 = cards.find(c => c.id === id1);
    const card2 = cards.find(c => c.id === id2);

    // ************* üöÄ ESTA ES LA VALIDACI√ìN CR√çTICA üöÄ *************
    if (card1.icon === card2.icon) {
      // --- ACUERDO (MATCH): DEJAR ABIERTAS PERMANENTEMENTE ---
      setMatches(m => m + 1);
      
      setCards(prevCards => 
          prevCards.map(card => 
            card.id === id1 || card.id === id2 
              ? { ...card, isMatched: true, isFlipped: true } 
              : card
          )
      );
      // Resetear estados
      setFlippedCards([]); 
      setIsBlocking(false); 

    } else {
      // --- DESACUERDO (NO MATCH): SE CIERRAN EN SEGUNDOS ---
      setAttemptsLeft(prev => prev - 1); // Pierde un intento
      
      const timer = setTimeout(() => {
        setCards(prevCards => 
          prevCards.map(card => 
            card.id === id1 || card.id === id2 
              ? { ...card, isFlipped: false } // Se tapan
              : card
          )
        );
        // Desbloquear
        setFlippedCards([]); 
        setIsBlocking(false); 
      }, FLIP_BACK_DELAY); 

      return () => clearTimeout(timer);
    }
  }, [flippedCards, cards]);


  // --- üöÄ EFECTO PRINCIPAL (Se ejecuta al voltear la segunda carta) üöÄ ---
  useEffect(() => {
    // Si ya tenemos 2 cartas volteadas, ejecutamos la comparaci√≥n
    if (flippedCards.length === 2) {
      compareCards();
    }
  }, [flippedCards, compareCards]);


  // --- L√ìGICA DE MANEJO DE CLIC EN UNA CARTA ---
  const handleCardClick = (id) => {
    // Bloquea el clic si hay un retardo activo o ya se seleccionaron dos cartas
    if (isBlocking || isGameOver || flippedCards.length === 2) return;
    
    setCards(prevCards => {
      const cardIndex = prevCards.findIndex(card => card.id === id);
      const card = prevCards[cardIndex];

      if (card.isFlipped || card.isMatched) return prevCards;

      const newCards = [...prevCards];
      newCards[cardIndex] = { ...card, isFlipped: true };
      setFlippedCards(prev => [...prev, id]);
      
      return newCards;
    });
  };


  // --- L√ìGICA DE FIN DEL JUEGO ---
  useEffect(() => {
      const totalPairs = CARD_ICONS.length;

      if (matches === totalPairs) {
          setIsGameOver(true);
      }
      
      if (attemptsLeft === 0 && matches < totalPairs) {
          setIsGameOver(true);
      }
  }, [attemptsLeft, matches]);


  // --- REINICIO DEL JUEGO ---
  const resetGame = () => {
    setCards(initializeCards());
    setFlippedCards([]);
    setMatches(0);
    setAttemptsLeft(MAX_ATTEMPTS);
    setIsBlocking(false);
    setIsGameOver(false);
  };

  // --- RENDERIZADO ---
  const totalPairs = CARD_ICONS.length;
  const hasWon = matches === totalPairs;

  return (
    <div className="memory-wrapper">
        <button className="back-button" onClick={() => setSelectedGame('home')}>
            ‚¨ÖÔ∏è Regresar al Men√∫
        </button>

        <div className="memory-container">
          <h1>Juego de Memoria üß†</h1>
          
          {(isGameOver) ? (
            <div className="game-status">
              <span style={{color: hasWon ? '#4CAF50' : '#FF4D4D'}}>
                  {hasWon ? 
                      `¬°Ganaste! üéâ Encontraste ${totalPairs} parejas.` : 
                      `¬°Perdiste! üò¢ Te quedaste sin intentos.`
                  }
              </span>
              <button onClick={resetGame} className="reset-button">Jugar de Nuevo</button>
            </div>
          ) : (
            <div className="game-status-info">
                Intentos: {MAX_ATTEMPTS - attemptsLeft} / {MAX_ATTEMPTS} | Aciertos: {matches} / {totalPairs}
            </div>
          )}

          <div 
            className={`memory-grid grid-4 ${isGameOver ? 'game-over-blur' : ''}`}
          >
            {cards.map(card => (
              <div 
                key={card.id} 
                className={`card ${card.isFlipped ? 'flipped' : ''} ${card.isMatched ? 'matched' : ''}`}
                onClick={() => handleCardClick(card.id)}
              >
                <div className="card-inner">
                  <div className="card-front">?</div>
                  <div className="card-back">{card.icon}</div>
                </div>
              </div>
            ))}
          </div>

          <div className="info-bar">
            <button onClick={resetGame} className="reset-button">Reiniciar Juego</button>
          </div>
        </div>

        <div className="ad-unit-memory"></div>
    </div>
  );
}

export default MemoryGame;
